(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[34406],{34406:n=>{n.exports='<table\n    tuiTable\n    [columns]="columns"\n>\n    <thead>\n        <tr tuiThGroup>\n            <th\n                *tuiHead="\'name\'"\n                tuiTh\n            >\n                Name\n            </th>\n            <th\n                *tuiHead="\'email\'"\n                tuiTh\n            >\n                E-mail\n            </th>\n            <th\n                *tuiHead="\'status\'"\n                tuiTh\n            >\n                Status\n            </th>\n            <th\n                *tuiHead="\'tags\'"\n                tuiTh\n                [sorter]="null"\n            >\n                Tags\n            </th>\n            <th\n                *tuiHead="\'actions\'"\n                tuiTh\n                [sorter]="null"\n            ></th>\n        </tr>\n    </thead>\n    <tbody\n        *tuiLet="users | tuiTableSort as sortedUsers"\n        tuiTbody\n        [data]="sortedUsers"\n    >\n        <tr\n            *ngFor="let item of sortedUsers; let index = index"\n            tuiTr\n        >\n            <td\n                *tuiCell="\'name\'"\n                tuiTd\n            >\n                {{ index + 1 }}. {{ item.name }}\n            </td>\n            <td\n                *tuiCell="\'email\'"\n                tuiTd\n            >\n                <a\n                    *ngIf="item.email"\n                    tuiLink\n                    [href]="\'mailto:\' + item.email"\n                >\n                    {{ item.email }}\n                </a>\n            </td>\n            <td\n                *tuiCell="\'status\'"\n                tuiTd\n            >\n                <div [class]="item.status">{{ item.status }}</div>\n            </td>\n            <td\n                *tuiCell="\'tags\'"\n                tuiTd\n            >\n                <tui-chip\n                    *ngFor="let tag of item.tags"\n                    size="xs"\n                    class="tui-space_right-1"\n                    [style.background]="tag | tuiAutoColor"\n                >\n                    {{ tag }}\n                </tui-chip>\n            </td>\n            <td\n                *tuiCell="\'actions\'"\n                tuiTd\n            >\n                <button\n                    appearance="flat"\n                    iconLeft="tuiIconTrash"\n                    size="s"\n                    tuiIconButton\n                    type="button"\n                    class="remove"\n                    [style.border-radius.%]="100"\n                    (click)="remove(item)"\n                >\n                    Remove\n                </button>\n            </td>\n        </tr>\n    </tbody>\n</table>\n'}}]);