(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[36838],{36838:n=>{n.exports='<div\n    tuiCarouselButtons\n    class="wrapper"\n>\n    <button\n        iconLeft="tuiIconChevronLeftLarge"\n        tuiIconButton\n        type="button"\n        (click)="carousel.prev()"\n    >\n        Previous\n    </button>\n    <tui-carousel\n        #carousel\n        class="carousel"\n    >\n        <div\n            *tuiItem\n            class="item"\n        >\n            <h2 class="title">Monty Python and the Holy Grail</h2>\n            <p>\n                King Arthur and his Knights of the Round Table embark on a surreal, low-budget search for the Holy\n                Grail, encountering many, very silly obstacles.\n            </p>\n            <div>\n                <button\n                    appearance="primary"\n                    tuiButton\n                    type="button"\n                >\n                    Buy ${{ 12.99 | tuiAmount: \'RUB\' | async }}\n                </button>\n                <button\n                    tuiButton\n                    type="button"\n                    class="tui-space_left-2"\n                >\n                    Rent ${{ 4.99 | tuiAmount: \'RUB\' | async }}\n                </button>\n            </div>\n        </div>\n        <div\n            *tuiItem\n            class="item"\n        >\n            <tui-loader />\n        </div>\n        <div\n            *tuiItem\n            class="item"\n            [style.text-align]="\'center\'"\n        >\n            <h2 class="title">Failed to load</h2>\n            <p>\n                <tui-avatar\n                    appearance="error"\n                    src="tuiIconClose"\n                />\n            </p>\n        </div>\n    </tui-carousel>\n    <button\n        iconLeft="tuiIconChevronRightLarge"\n        tuiIconButton\n        type="button"\n        (click)="carousel.next()"\n    >\n        Next\n    </button>\n</div>\n'}}]);