(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[43035],{43035:n=>{n.exports='<h3>Multi select control</h3>\n<button\n    appearance="flat"\n    tuiChevron\n    tuiIconButton\n    type="button"\n    [tuiAppearanceState]="control ? \'hover\' : null"\n    [tuiDropdown]="content"\n    [tuiDropdownMaxHeight]="500"\n    [(tuiDropdownOpen)]="control"\n></button>\n<ng-template #content>\n    <tui-data-list [style.width.rem]="12">\n        <tui-opt-group\n            tuiMultiSelectGroup\n            [(ngModel)]="value"\n        >\n            <tui-opt-group label="Main dishes menu with long label">\n                <button\n                    *ngFor="let burger of burgers"\n                    tuiOption\n                    [value]="burger"\n                >\n                    {{ burger }}\n                </button>\n            </tui-opt-group>\n            <tui-opt-group label="Drinks">\n                <button\n                    *ngFor="let drink of drinks"\n                    tuiOption\n                    [value]="drink"\n                >\n                    {{ drink }}\n                </button>\n            </tui-opt-group>\n        </tui-opt-group>\n    </tui-data-list>\n</ng-template>\n<p>{{ value }}</p>\n<h3>Separate toggles</h3>\n<button\n    appearance="flat"\n    tuiChevron\n    tuiIconButton\n    type="button"\n    [tuiAppearanceState]="label ? \'hover\' : null"\n    [tuiDropdown]="separate"\n    [tuiDropdownMaxHeight]="500"\n    [(tuiDropdownOpen)]="label"\n></button>\n<ng-template #separate>\n    <tui-data-list [style.width.rem]="10">\n        <label tuiOption>\n            First\n            <input\n                tuiCheckbox\n                type="checkbox"\n                [(ngModel)]="first"\n            />\n        </label>\n        <label tuiOption>\n            Second\n            <input\n                tuiSwitch\n                type="checkbox"\n                [(ngModel)]="second"\n            />\n        </label>\n    </tui-data-list>\n</ng-template>\n'}}]);