"use strict";(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[45754],{45754:e=>{e.exports='<tui-files>\n    <tui-file\n        *ngFor="let file of files"\n        state="normal"\n        [file]="file"\n        [showDelete]="control.enabled"\n    />\n\n    <tui-file\n        *ngFor="let file of rejectedFiles"\n        state="error"\n        [file]="file"\n        [showDelete]="control.enabled"\n    />\n\n    <tui-file\n        *ngIf="loadingFile && !isE2E"\n        state="loading"\n        [file]="loadingFile"\n        [showDelete]="control.enabled"\n        (remove)="removeLoading()"\n    />\n</tui-files>\n\n<h4>With link</h4>\n<tui-files>\n    <a\n        *tuiItem\n        rel="noreferrer noopener"\n        state="normal"\n        target="_blank"\n        tuiFile\n        [file]="fileWithLink"\n        [href]="fileWithLink.src"\n        [showDelete]="control.enabled"\n    ></a>\n</tui-files>\n\n<h4>With deleted state</h4>\n<tui-files>\n    <tui-file\n        *ngFor="let file of removedFiles"\n        size="l"\n        state="deleted"\n        [file]="file"\n        [showDelete]="control.enabled"\n    >\n        <button\n            tuiLink\n            type="button"\n            (click)="restore(file)"\n        >\n            Restore\n        </button>\n    </tui-file>\n    <tui-file\n        *ngFor="let file of restoredFiles"\n        size="l"\n        state="normal"\n        [file]="file"\n        [leftContent]="icon"\n        [showDelete]="control.enabled"\n        (remove)="remove(file)"\n    />\n</tui-files>\n\n<ng-template #icon>\n    <tui-svg src="tuiIconFileLarge" />\n</ng-template>\n'}}]);