(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[52029],{52029:n=>{n.exports='<ng-container tuiTableFilters>\n    <form [formGroup]="form">\n        <tui-input-number\n            formControlName="balance"\n            tuiTableFilter="balance"\n            [tuiGenericFilter]="filter"\n        >\n            Minimal balance\n        </tui-input-number>\n        <label class="toggle">\n            <input\n                size="s"\n                tuiSwitch\n                type="checkbox"\n                [ngModel]="form.enabled"\n                [ngModelOptions]="{standalone: true}"\n                [showIcons]="false"\n                (ngModelChange)="onToggle($event)"\n            />\n            Enable filtering\n        </label>\n    </form>\n    <table\n        tuiTable\n        class="table"\n        [columns]="columns"\n    >\n        <thead>\n            <tr tuiThGroup>\n                <th tuiTh>Name</th>\n                <th tuiTh>Balance</th>\n            </tr>\n        </thead>\n        <tbody tuiTbody>\n            <tr\n                *ngFor="let item of data | tuiTableFilters | async"\n                tuiTr\n            >\n                <td\n                    *tuiCell="\'name\'"\n                    tuiTd\n                >\n                    {{ item.name }}\n                </td>\n                <td\n                    *tuiCell="\'balance\'"\n                    tuiTd\n                >\n                    {{ item.balance | tuiFormatNumber | async }}\n                </td>\n            </tr>\n        </tbody>\n    </table>\n</ng-container>\n'}}]);