"use strict";(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[64697],{64697:n=>{n.exports='<p>Right-click any table row.</p>\n\n<table class="tui-table">\n    <tbody>\n        <tr class="tui-table__tr tui-table__tr_hover_disabled">\n            <th\n                *ngFor="let column of tableColumns"\n                class="tui-table__th"\n            >\n                {{ column }}\n            </th>\n        </tr>\n        <tr\n            *ngFor="let rowInfo of tableData"\n            #dropdown="tuiDropdown"\n            tuiDropdownContext\n            class="tui-table__tr"\n            [tuiDropdown]="contextMenu"\n        >\n            <td\n                *ngFor="let value of getObjectValues(rowInfo)"\n                class="tui-table__td"\n            >\n                {{ value }}\n            </td>\n\n            <ng-template #contextMenu>\n                <tui-data-list\n                    role="menu"\n                    tuiDataListDropdownManager\n                    class="context-menu"\n                >\n                    <button\n                        *ngFor="let item of menuItems"\n                        tuiOption\n                        (click)="printToConsole(item.title, rowInfo); dropdown.toggle(false)"\n                    >\n                        {{ item.title }}\n                        <tui-svg\n                            class="icon"\n                            [src]="item.iconName"\n                        />\n                    </button>\n\n                    <button\n                        iconRight="tuiIconChevronRight"\n                        tuiDropdownAlign="right"\n                        tuiDropdownSided\n                        tuiOption\n                        [tuiDropdown]="nestedMenu"\n                    >\n                        More\n                    </button>\n                </tui-data-list>\n\n                <ng-template #nestedMenu>\n                    <tui-data-list>\n                        <button\n                            *ngFor="let option of moreOptions"\n                            tuiOption\n                        >\n                            {{ option }}\n                        </button>\n                    </tui-data-list>\n                </ng-template>\n            </ng-template>\n        </tr>\n    </tbody>\n</table>\n'}}]);