(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[77707],{77707:n=>{n.exports='<tui-input-phone\n    *tuiLet="items$ | async as items"\n    [allowText]="true"\n    [tuiTextfieldCustomContent]="avatar"\n    [(ngModel)]="value"\n    (searchChange)="onSearch($event)"\n>\n    {{ placeholder$ | async }}\n    <ng-container *ngIf="items?.length">\n        <tui-data-list *tuiDataList>\n            <button\n                *ngFor="let item of items"\n                tuiOption\n                [disabled]="item.disabled"\n                [value]="item.phone"\n                (click)="onClick(item)"\n            >\n                <tui-avatar\n                    size="s"\n                    class="tui-space_right-3"\n                    [src]="item.avatarUrl"\n                >\n                    {{ item.firstName }}\n                </tui-avatar>\n                <span class="user">\n                    <span>{{ item.firstName }} {{ item.lastName }}</span>\n                    <span class="phone">{{ item.phone }}</span>\n                </span>\n            </button>\n        </tui-data-list>\n    </ng-container>\n</tui-input-phone>\n<ng-template #avatar>\n    <tui-avatar\n        *ngIf="user$ | async as user"\n        size="s"\n        [src]="user.avatarUrl"\n    >\n        {{ user.firstName }}\n    </tui-avatar>\n</ng-template>\n<p>Value: {{ value }}</p>\n'}}]);