(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[80261],{80261:n=>{n.exports='<button\n    tuiButton\n    tuiDropdownHover\n    type="button"\n    [tuiDropdown]="dropdown"\n    [(tuiDropdownOpen)]="open"\n>\n    Dropdown hover\n    <ng-template\n        #dropdown\n        let-activeZone\n    >\n        <div [style.padding]="\'0 1rem\'">\n            <p>\n                There is\n                <code>let-activeZone</code>\n                in dropdown template context to work with nested dropdowns\n            </p>\n\n            <tui-select\n                class="margin"\n                [formControl]="selected"\n                [tuiActiveZoneParent]="activeZone"\n            >\n                Nested select\n                <tui-data-list-wrapper\n                    *tuiDataList\n                    [items]="selectItems"\n                />\n            </tui-select>\n\n            <p>\n                <button\n                    size="s"\n                    tuiButton\n                    tuiDropdownHover\n                    tuiDropdownOpen\n                    type="button"\n                    [tuiActiveZoneParent]="activeZone"\n                    [tuiDropdown]="content"\n                >\n                    Nested dropdown hover\n                </button>\n                <ng-template #content>\n                    <p class="tui-space_horizontal-2">Nested content!</p>\n                </ng-template>\n            </p>\n        </div>\n\n        <tui-data-list>\n            <button\n                *ngFor="let item of items"\n                tuiOption\n            >\n                {{ item }}\n            </button>\n        </tui-data-list>\n    </ng-template>\n</button>\n'}}]);