(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[89175],{89175:n=>{n.exports='<button\n    size="m"\n    tuiButton\n    type="button"\n    class="tui-space_bottom-4"\n    (click)="show()"\n>\n    Show preview\n</button>\n\n<ng-template\n    #preview\n    let-preview\n>\n    <tui-preview\n        [rotatable]="!(contentUnavailable$ | async)"\n        [zoomable]="!(contentUnavailable$ | async) && !(loading$ | async)"\n    >\n        <tui-preview-title>{{ title$ | async }}</tui-preview-title>\n        <tui-preview-pagination\n            [index]="index$$.value"\n            [length]="items.length"\n            (indexChange)="index$$.next($event)"\n        />\n\n        <button\n            iconLeft="tuiIconDownload"\n            tuiIconButton\n            tuiPreviewAction\n            type="button"\n            (click)="download()"\n        >\n            Download\n        </button>\n\n        <button\n            iconLeft="tuiIconClose"\n            tuiIconButton\n            tuiPreviewAction\n            type="button"\n            (click)="preview.complete()"\n        >\n            Close\n        </button>\n\n        <ng-container *ngIf="contentUnavailable$ | async">\n            <div class="t-container">\n                <tui-svg\n                    src="tuiIconFileLarge"\n                    class="t-icon"\n                />\n                <div>Preview unavailable</div>\n            </div>\n        </ng-container>\n\n        <img\n            *ngIf="imageSrc$ | async as src"\n            alt="img source"\n            height="512"\n            width="512"\n            [src]="src"\n        />\n\n        <tui-loader\n            *ngIf="loading$ | async"\n            size="xl"\n            class="t-loader"\n        />\n    </tui-preview>\n</ng-template>\n'}}]);