(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[93750],{93750:n=>{n.exports='<tui-items-with-more\n    tuiGroup\n    [collapsed]="true"\n>\n    <ng-container *ngFor="let item of items; let index = index">\n        <label\n            *tuiItem\n            appearance=""\n            tuiBlock="m"\n            [style.border-radius]="\'inherit\'"\n        >\n            {{ item }}\n            <input\n                tuiBlock\n                type="checkbox"\n                [(ngModel)]="value[index]"\n            />\n        </label>\n    </ng-container>\n    <ng-template\n        let-lastIndex\n        tuiMore\n    >\n        <button\n            appearance="whiteblock"\n            size="m"\n            tuiButton\n            tuiDropdownAlign="right"\n            tuiDropdownOpen\n            type="button"\n            class="item"\n            [tuiDropdown]="dropdown"\n        >\n            More\n            <tui-badge class="tui-space_left-2">{{ items.length - lastIndex - 1 }}</tui-badge>\n        </button>\n        <ng-template #dropdown>\n            <tui-data-list size="l">\n                <ng-container *ngFor="let item of items; let index = index">\n                    <button\n                        *ngIf="index > lastIndex"\n                        tuiOption\n                        (click)="value[index] = !value[index]"\n                    >\n                        {{ item }}\n                        <tui-svg\n                            src="tuiIconCheck"\n                            class="tui-space_left-2"\n                            [class._hidden]="!value[index]"\n                        />\n                    </button>\n                </ng-container>\n            </tui-data-list>\n        </ng-template>\n    </ng-template>\n</tui-items-with-more>\n'}}]);